mapper.insert:
INSERT INTO TBL_BOARD(
	bno
	, title
	, content
	, writer
) VALUES (
	SEQ_BOARD.NEXTVAL
	, #{title}
	, #{content}
	, #{writer}
)


	SELECT SEQ_BOARD.NEXTVAL FROM DUAL


INSERT INTO TBL_BOARD(
	bno
	, title
	, content
	, writer
) VALUES (
	#{bno}
	, #{title}
	, #{content}
	, #{writer}
)

mapper.update:
UPDATE TBL_BOARD SET
title = #{title}
, content = #{content}
, writer = #{writer}
, updateDate = SYSDATE
WHERE bno = #{bno}
